"""Bu programin qrafik arauzu testedilerek islemisem bezi melumatlarHava funksiyasi internet uzerindenarasdirilaraq yazilmisdir openweather.orgsayti vasitesile"""import json  # Ferqli yerlerde istifade ucunimport requests  # Internetden cekeceyimiz melumat ucunimport tkinter as tkfrom tkinter import *from tkinter import messageboxdef  Qrafika():    Seher=Label(pencere,text="Şəhəri daxil edin : ",bg="red")    Seher.grid(row=0,column=0,padx=10,pady=5) #Sutun sifir setir sifir geometri teyini    SeherGiris=Entry(pencere,width=60,textvariable=SeherAdi) # Olculeri deyise bilersiniz    SeherGiris.grid(row=0,column=1,padx=10,pady=5)    Axtar=Button(pencere,text="Axtarış et",command=Hava, bg="red") # Axtaris butonunu elave eledik    Axtar.grid(row=1,column=0,padx=5,pady=5,columnspan=2)  # geometry elave etdik (bunlar evvelceden test ortaminda islenir)    Sil=Button(pencere,text="Sil",command=Silmek,width=10,bg="red")  # Melumarlari temizlemek ucun Sil butonunu elave eledik    Sil.grid(row=1,column=1,padx=10,pady=5,columnspan=2) # Geometrisini elave eledik    SeherinKoordinanti=Label(pencere,text="Şəhərin Koordinantı : ",bg="red") # Daxil etdiyimiz seherin koordinantlarini gormek ucun Koordinant elave etdik    SeherinKoordinanti.grid(row=2,column=0,padx=10,pady=5)    pencere.Seherinkoordinanti=Entry(pencere,width=60)  # Enini 60 teyin eledik (width)    pencere.Seherinkoordinanti.grid(row=2,column=1,padx=10,pady=5)    Temperatur=Label(pencere,text="Temperatur : ",bg="red") # Temperaturu goruntulemek ucun Temperatur deye label elave eledik    Temperatur.grid(row=3,column=0,padx=10,pady=5)    pencere.temperatura=Entry(pencere,width=60)  # Temperaturu elave etmek ucun giris elave eledik    pencere.temperatura.grid(row=3,column=1,padx=10,pady=5)    Rutubet=Label(pencere,text="Rütubət : ",bg="red") #Rutubet gostermek ucun elave eledik    Rutubet.grid(row=4,column=0,padx=10,pady=5)    pencere.rutubetgiris=Entry(pencere,width=60)  # Rutubete giris eave eledik    pencere.rutubetgiris.grid(row=4,column=1,padx=10,pady=5) #geometry    Kulek=Label(pencere,text="Külək : ",bg="red")  # Kuleyi oyrenmek ucun label yaratdiq    Kulek.grid(row=5,column=0,padx=10,pady=5)    pencere.kulekgiris=Entry(pencere,width=60)    pencere.kulekgiris.grid(row=5,column=1,padx=10,pady=5)    Atmosfer=Label(pencere,text="Atmosfer : ",bg="red")    Atmosfer.grid(row=6,column=0,padx=10,pady=5)    pencere.atmosfergiris=Entry(pencere,width=60)    pencere.atmosfergiris.grid(row=6,column=1,padx=10,pady=5)    HavaDurumu=Label(pencere,text="Hava durumu : ",bg="red") # Hava durumunu goruntulemek ucun elave etdik    HavaDurumu.grid(row=7,column=0,padx=10,pady=5)    pencere.havadurumu=Entry(pencere,width=60)   # Hava durumuna giris elave eledik    pencere.havadurumu.grid(row=7,column=1,padx=10,pady=5)  # geometrydef Hava():   # Bu funksiya yazilarken internetden melumat alinmisdir.Arasdirilarak komekli sekilde funksiya teyin edilib.       ACAR="92433bc01e940e75157e39048f261bf9"  # Api keyi openweathermap.org saytina girerek qeydiyyatdan kecib alirsan pulsuz olaraq emaile gonderilir    weatherURL="http://api.openweathermap.org/data/2.5/weather?"  # Sayti deyiskene atiriq    cityname=SeherAdi.get()    requestURL=weatherURL+"appid="+ACAR+"&q="+cityname+"&units=metric"    response=requests.get(requestURL)    weatherResponse=response.json()    print(json.dumps(weatherResponse,indent=2))  # json modulundan istifade edilerek melumatlari json formatinda print edirik  (indent=2 olaraq)    if weatherResponse["cod"]!="404":        weatherPARA=weatherResponse["main"]        coordinates=weatherResponse["coord"]        latitude=str(coordinates["lat"])        longitude=str(coordinates["lon"])        wind=weatherResponse["wind"]        windSpeed=str(wind['speed'])        if 'deg' in wind.keys():            windDirect=str(wind['deg'])        else:            windDirect=''              temperature=str(weatherPARA["temp"])        pressure=str(weatherPARA["pressure"])        humidity=str(weatherPARA["humidity"])        weatherDesc=weatherResponse["weather"]        weatherDescription=weatherDesc[0]["description"]        pencere.Seherinkoordinanti.insert("0","Şimal Enliyi :  " +latitude+ "  Şimal Uzunluğu :  "  +longitude) # Koordinantlarin simal enliyi ve uzunluyu teyin edilir        pencere.temperatura.insert("0",temperature+ "  Dərəcə")  # Temperaturun derecesi girilir        pencere.rutubetgiris.insert("0",str(humidity)+ " %")        pencere.kulekgiris.insert("0","Sürəti  :   " + windSpeed+ "  metir/san"  +  "  Istiqaməti  :   " +windDirect+ "    Dərəcə")  # Kuleyin melumatlari yazdirilir        pencere.atmosfergiris.insert("0",pressure+" hPa")  # Atmosfer teyziqi qeyd olunub        pencere.havadurumu.insert("0",weatherDescription)                         else:        messagebox.showerror("Xəta","Şəhər tapilmadi")  # Tkinter istifade ederek istifadeciye mesaj gonderirikdef Silmek():  #Melumatlari temizlemek ucun Silmek funksiyasini elave edirik    SeherAdi.set('')    pencere.Seherinkoordinanti.delete(0,END)    pencere.temperatura.delete(0,END)    pencere.rutubetgiris.delete(0,END)    pencere.kulekgiris.delete(0,END)    pencere.atmosfergiris.delete(0,END)    pencere.havadurumu.delete(0,END) pencere=tk.Tk()   # tkinteri pencere deyiskenine atayiriqpencere.title("Hava haqqinda melumat")  # Proqramin adini teyin edirikpencere.config(background="black")  # Arxa gorunusu mavi olaraq qeyd edirikpencere.geometry("700x400")  # Olculerini teyin edirikpencere.resizable(False,False) # Ekran olculerini ssabit saxlayiriq (deyisdirilmesin deye)SeherAdi=StringVar()  #Yazi tipiQrafika() # Funksiyani tanidiriq cagiribpencere.mainloop()  #Programi islek veziyyete getririk                                                      